var Cassandra = require('oae-util/lib/cassandra');

var ActivityAPI = require('oae-activity');
var ActivityPush = require('./internal/push');

// Register some of the default streams
require('./activity');

// Bind the notification event listeners
require('./internal/notifications');

// Bind the email event listeners
require('./internal/email');

module.exports = function(config, callback) {
    ensureSchema(function(err) {
        if (err) {
            return callback(err);
        }

        ActivityAPI.refreshConfiguration(config.activity, function(err) {
            if (err) {
                return callback(err);
            }

            // Configure the push notifications
            ActivityPush.init(callback);
        });
    });
};

/**
 * Ensure that the all of the activity-related schemas are created. If they already exist, this method will not do anything.
 *
 * @param  {Function}    callback       Invoked when the process completes
 * @param  {Object}      callback.err   Error object, containing the error message if any
 * @api private
 */
var ensureSchema = function(callback) {
    Cassandra.createColumnFamilies({
        'ActivityStreams': 'CREATE TABLE "ActivityStreams" ("activityStreamId" text, "activityId" text, "activity" text, PRIMARY KEY ("activityStreamId", "activityId")) WITH COMPACT STORAGE',
        'EmailBuckets': 'CREATE TABLE "EmailBuckets" ("bucketId" text, "userId" text, PRIMARY KEY ("bucketId", "userId"))'
    }, callback);
};
