var OaeUtil = require('oae-util/lib/util');

var DEFAULT_ACTIVITY_TTL = 2 * 7 * 24 * 60 * 60;    // 2 weeks (in seconds)
var DEFAULT_AGGREGATE_IDLE_EXPIRY = 3 * 60 * 60;    // 3 hours (in seconds)
var DEFAULT_AGGREGATE_MAX_EXPIRY = 24 * 60 * 60;    // 1 day (in seconds)
var DEFAULT_NUMBER_OF_PROCESSING_BUCKETS = 5;
var DEFAULT_COLLECTION_EXPIRY = 60;                 // 1 minute (in seconds)
var DEFAULT_MAX_CONCURRENT_COLLECTIONS = 3;
var DEFAULT_COLLECTION_POLLING_FREQUENCY = 5;       // 5 seconds
var DEFAULT_COLLECTION_BATCH_SIZE = 500;
var DEFAULT_ALLOW_ANON_COLLECTION = false;

var config = {};

/**
 * Refresh the activities configuration. For list of available properties @see ActivityAPI#refreshConfiguration.
 *
 * @param   {Object}    [config]    The configuration options with which to refresh. See the `config.activity` object in the base `./config.js` for more information
 */
var refreshConfiguration = module.exports.refreshConfiguration = function(_config) {
    _config = _config || {};
    config = {
        'processActivityJobs': (_config.processActivityJobs !== false),
        'activityTtl': OaeUtil.getIntParam(_config.activityTtl, DEFAULT_ACTIVITY_TTL),
        'numberOfProcessingBuckets': OaeUtil.getIntParam(_config.numberOfProcessingBuckets, DEFAULT_NUMBER_OF_PROCESSING_BUCKETS),
        'aggregateIdleExpiry': OaeUtil.getIntParam(_config.aggregateIdleExpiry, DEFAULT_AGGREGATE_IDLE_EXPIRY),
        'aggregateMaxExpiry': OaeUtil.getIntParam(_config.aggregateMaxExpiry, DEFAULT_AGGREGATE_MAX_EXPIRY),
        'collectionExpiry': OaeUtil.getIntParam(_config.collectionExpiry, DEFAULT_COLLECTION_EXPIRY),
        'maxConcurrentCollections': OaeUtil.getIntParam(_config.maxConcurrentCollections, DEFAULT_MAX_CONCURRENT_COLLECTIONS),
        'collectionPollingFrequency': OaeUtil.getIntParam(_config.collectionPollingFrequency, DEFAULT_COLLECTION_POLLING_FREQUENCY),
        'collectionBatchSize': OaeUtil.getIntParam(_config.collectionBatchSize, DEFAULT_COLLECTION_BATCH_SIZE)
    };
    
    return getConfig();
};

/**
 * @return {Object} the activities configuration.
 */
var getConfig = module.exports.getConfig = function() {
    return config;
};

