/**
 * Holds the data points that can be used to select the correct text
 * for an annotation in a document
 *
 * @typedef Range
 * @type {Object}
 * @property  {String}     start           An XPath expression selecting the DOM element where the selection starts
 * @property  {String}     end             An XPath expression selecting the DOM element where the selection ends
 * @property  {Number}     startOffset     The numeric text offset in the start element where the selection starts
 * @property  {Number}     endOffset       The numeric text offset in the end element where the selection end
 */

/**
 * The annotation data modal
 *
 * @param  {String}     id              The unique ID for the annotation. This should be a composite of the page number, followed by `#` and a sufficiently random string
 * @param  {String}     contentId       The content ID this annotation belongs to
 * @param  {String}     revisionId      The revision ID this annotation belongs to
 * @param  {Number}     created         The milliseconds since the epoch when the annotation was created
 * @param  {String}     createdBy       The ID of the user who made the annotation
 * @param  {Number}     [updated]       The milliseconds since the epoch when the annotation was last updated
 * @param  {String}     [updatedBy]     The ID of the user who made the last update to the annotation
 * @param  {Number}     pageNumber      The page number on which the annotation was made
 * @param  {String}     text            The annotation text
 * @param  {String}     quote           The text that was selected
 * @param  {Range[]}    ranges          An array of selection where the annotation is applied on, usually only one
 */
var Annotation = module.exports.Annotation = function(id, contentId, revisionId, created, createdBy, updated, updatedBy, pageNumber, text, quote, ranges) {
    var that = {};
    that.id = id;
    that.contentId = contentId;
    that.revisionId = revisionId;
    that.created = created;
    that.createdBy = createdBy;
    that.updated = updated;
    that.updatedBy = updatedBy;
    that.pageNumber = pageNumber;
    that.text = text;
    that.quote = quote;
    that.ranges = ranges;
    return that;
};
