var Cassandra = require('oae-util/lib/cassandra');

/**
 * Ensure that the session table gets created. If they already exist, this method will not
 * do anything
 * @param {Function(err)}    callback       A function(err), specifying whether or not there was an error creating the schema.
 * @param {Object}           callback.err   Error object, containing the error message
 */
module.exports.ensureSchema = function(callback) {
    callback = callback || function() {};
    Cassandra.createColumnFamily('Sessions', "CREATE TABLE Sessions(sid text PRIMARY KEY, data text);", callback);
};

/**
 * Drop the session schema and all of its data.
 *
 * @param {Function(err)}    callback       A function(err), specifying whether or not there was an error dropping the schema.
 * @param {Object}           callback.err   Error object, containing the error message
 */
module.exports.dropSchema = function(callback) {
    callback = callback || function() {};
    Cassandra.dropColumnFamily('Sessions', callback);
};
