var FacebookStrategy = require('passport-facebook').Strategy;

var ConfigAPI = require('oae-config');
var log = require('oae-logger').logger('oae-authentication');

var AuthenticationAPI = require('oae-authentication');
var AuthenticationConfig = ConfigAPI.config('oae-authentication');
var AuthenticationConstants = require('oae-authentication/lib/constants').AuthenticationConstants;
var AuthenticationUtil = require('oae-authentication/lib/util');


module.exports = function() {
    var strategy = {};

    /**
     * @see oae-authentication/lib/strategy#shouldBeEnabled
     */
    strategy.shouldBeEnabled = function(tenantAlias) {
        return AuthenticationConfig.getValue(tenantAlias, AuthenticationConstants.providers.FACEBOOK, 'enabled');
    };

    /**
     * @see oae-authentication/lib/strategy#getPassportStrategy
     */
    strategy.getPassportStrategy = function(tenant) {
        // We fetch the config values *in* the getPassportStrategy so it can be re-configured at run-time.
        var clientID = AuthenticationConfig.getValue(tenant.alias, AuthenticationConstants.providers.FACEBOOK, 'appid');
        var clientSecret = AuthenticationConfig.getValue(tenant.alias, AuthenticationConstants.providers.FACEBOOK, 'secret');

        var passportStrategy = new FacebookStrategy({
            'clientID': clientID,
            'clientSecret': clientSecret,
            'passReqToCallback': true,
            'profileFields': ['id', 'displayName', 'photos', 'emails'],
            'callbackURL': AuthenticationUtil.constructCallbackUrl(tenant, AuthenticationConstants.providers.FACEBOOK)
        }, function(req, accessToken, refreshToken, profile, done) {
            log().trace({'tenant': tenant, 'profile': profile}, 'Received Facebook authentication callback');

            var externalId = profile.id;
            var displayName = profile.displayName;
            var opts = {'locale': profile._json.locale};

            var picture = profile['_json']['picture'];
            if (picture && picture.data && picture.data.url && !picture.data.is_silhouette) {
                opts.smallPictureUri = 'remote:' + picture.data.url;
                opts.mediumPictureUri = 'remote:' + picture.data.url;
            }

            if (profile.emails && profile.emails.length > 0 && profile.emails[0].value) {
                opts.email = profile.emails[0].value;
            }

            AuthenticationUtil.handleExternalGetOrCreateUser(req, AuthenticationConstants.providers.FACEBOOK, externalId, null, displayName, opts, done);
        });
        return passportStrategy;
    };

    // Register our strategy.
    AuthenticationAPI.registerStrategy(AuthenticationConstants.providers.FACEBOOK, strategy);
};
