var passport = require('passport');

var OAE = require('oae-util/lib/oae');

var AuthenticationConstants = require('oae-authentication/lib/constants').AuthenticationConstants;
var AuthenticationUtil = require('oae-authentication/lib/util');

/**
 * Performs LDAP authentication.
 *
 * @param  {Request}    req     The express request object
 * @param  {Response}   res     The express response object
 * @api private
 */
var _handleLDAPAuthentication = function(req, res, next) {
    var strategyId = AuthenticationUtil.getStrategyId(req.tenant, AuthenticationConstants.providers.LDAP);
    var errorHandler = AuthenticationUtil.handlePassportError(req, res, next);
    passport.authenticate(strategyId)(req, res, errorHandler);
};

/*!
 * Log in with LDAP
 */
OAE.tenantRouter.on('post', '/api/auth/ldap', _handleLDAPAuthentication, function(req, res, next) {
    // This callback only gets called when we log in succesfully.
    return res.send(200, req.user);
});
