var Cassandra = require('oae-util/lib/cassandra');

module.exports = function(app, callback) {
    ensureSchema(callback);
}

/**
 * Ensure the Authz schema exists.
 *
 * @param {Function(err)}    callback       A function(err), specifying whether or not there was an error creating the schema.
 * @param {Object}           callback.err   Error object, containing the error message
 */
var ensureSchema = function(callback) {
    Cassandra.createColumnFamilies({
        'AuthzRoles': "CREATE COLUMNFAMILY AuthzRoles (principalId text PRIMARY KEY) WITH comment='Holds all of the role assigments between resources and principals';",
        'AuthzGroupMembers': "CREATE COLUMNFAMILY AuthzGroupMembers (groupId text PRIMARY KEY) WITH comment='Holds all the members for a group in one row';"
    }, callback);
};