var Cassandra = require('oae-util/lib/cassandra');

/**
 * Ensure the Authz schema exists.
 *
 * @param {Function(err)}    callback       A function(err), specifying whether or not there was an error creating the schema.
 * @param {Object}           callback.err   Error object, containing the error message
 */
module.exports.ensureSchema = function(callback) {
    callback = callback || function() {};
    Cassandra.createColumnFamilies({
        'AuthzRoles': "CREATE TABLE AuthzRoles (principalId text PRIMARY KEY) WITH comment='Holds all of the role assigments between resources and principals';",
        'AuthzGroupMembers': "CREATE TABLE AuthzGroupMembers (groupId text PRIMARY KEY) WITH comment='Holds all the members for a group in one row';"
    }, callback);
};

/**
 * Drop the Authz schema and all of its data.
 *
 * @param {Function} callback A function(err), specifying whether or not there was an error dropping the schema
 */
module.exports.dropSchema = function(callback) {
    callback = callback || function() {};
    Cassandra.dropColumnFamilies(['AuthzRoles', 'AuthzGroupMembers'], callback);
};