var CollectionsConstants = module.exports.CollectionsConstants = {};

CollectionsConstants.roles = {
    // Determines not only all known roles, but the ordered priority they take as the "effective" role. (e.g., if
    // you are both a viewer and a manager, your effective role is "manager", so it must be later in the list)
    'ALL_PRIORITY': ['viewer', 'manager'],

    'MANAGER': 'manager',
    'VIEWER': 'viewer'
};

CollectionsConstants.events = {
    'CREATED_COLLECTION': 'createdCollection',
    'UPDATED_COLLECTION': 'updatedCollection',
    'UPDATED_COLLECTION_MEMBERS': 'updatedCollectionMembers',
    'ADDED_CONTENT_ITEMS': 'addedContentItem'
};

CollectionsConstants.library = {
    'COLLECTIONS_LIBRARY_INDEX_NAME': 'collections:collections',
    'CONTENT_LIBRARY_INDEX_NAME': 'collections:content'
};
