var _ = require('underscore');

var LibraryAPI = require('oae-library');
var log = require('oae-logger').logger('oae-collections-contentlibrary');

var CollectionsConstants = require('../constants').CollectionsConstants;

/**
 * Get the ids of the content items in the content library of a collection
 *
 * @param  {Collection}     collection              The collection whose content library to list
 * @param  {String}         visibility              The effective library visibility to list
 * @param  {Object}         [opts]                  Optional arguments for listing the library items
 * @param  {String}         [opts.start]            The token that indicates from where to start listing items
 * @param  {Number}         [opts.limit]            The maximum number of items to list
 * @param  {Function}       callback                Standard callback function
 * @param  {Object}         callback.err            An error that occurred, if any
 * @param  {String[]}       callback.contentIds     The content ids in the specified library
 * @param  {String}         callback.nextToken      The token to use for the `start` parameter for the next invocation to get the next page of results. If `null`, indicates that there are no more items to list
 */
var list = module.exports.list = function(collection, visibility, opts, callback) {
    LibraryAPI.Index.list(CollectionsConstants.library.CONTENT_LIBRARY_INDEX_NAME, collection.groupId, visibility, {'start': opts.start, 'limit': opts.limit}, callback);
};

/**
 * Insert all content items into the library of the provided collection
 *
 * @param  {Collection}     collection      The collection in which to insert the content items
 * @param  {Content[]}      contentItems    The content items to add to the collection content library
 * @param  {Function}       callback        Standard callback function
 * @param  {Object}         callback.err    An error that occurred, if any
 */
var insert = module.exports.insert = function(collection, contentItems, callback) {
    callback = callback || function(err) {
        if (err) {
            log().error({
                'err': err,
                'contentIds': _.pluck(contentItems, 'id'),
                'collectionId': collection.id,
                'collectionGroupId': collection.groupId
            }, 'Error inserting content items into collection library');
        }
    };

    _insert(collection, contentItems.slice(), callback);
};

/**
 * Remove the content items from the content library of the specified collection
 *
 * @param  {Collection}     collection      The collection from which to remove the content items
 * @param  {String[]}       contentIds      The content item ids to remove from the collection
 * @param  {Function}       callback        Standard callback function
 * @param  {Object}         callback.err    An error that occurred, if any
 */
var remove = module.exports.remove = function(collection, contentIds, callback) {
    callback = callback || function(err) {
        if (err) {
            log().error({
                'err': err,
                'contentIds': contentIds,
                'collectionId': collection.id,
                'collectionGroupId': collection.groupId
            }, 'Error removing content items from collection library');
        }
    };

    _remove(collection, contentIds.slice(), callback);
};

/**
 * Insert all content items into the library of the provided collection. This function is
 * destructive to the `contentItems` parameter as it iterates through content items to insert
 *
 * @param  {Collection}     collection      The collection in which to insert the content items
 * @param  {Content[]}      contentItems    The content items to add to the collection content library
 * @param  {Function}       callback        Standard callback function
 * @param  {Object}         callback.err    An error that occurred, if any
 * @api private
 */
var _insert = function(collection, contentItems, callback) {
    if (_.isEmpty(contentItems)) {
        // When there are no more items to insert, return to the caller
        return callback();
    }

    // Insert the item into the library index
    var contentItem = contentItems.pop();
    var effectiveLibraryVisibilities = LibraryAPI.Authz.resolveEffectiveLibraryVisibilities([collection.groupId], contentItem);
    LibraryAPI.Index.insert(CollectionsConstants.library.CONTENT_LIBRARY_INDEX_NAME, effectiveLibraryVisibilities, contentItem.id, null, function(err) {
        if (err) {
            log().warn({
                'err': err,
                'indexName': CollectionsConstants.library.CONTENT_LIBRARY_INDEX_NAME,
                'collectionId': collection.id,
                'collectionGroupId': collection.groupId,
                'contentId': contentItem.id,
                'visibilities': effectiveLibraryVisibilities
            }, 'An error occurred while inserting a content item into a collection library');
        }

        // Recursively insert the next item
        return _insert(collection, contentItems, callback);
    });
};

/**
 * Remove the content items from the content library of the specified collection. This function is
 * destructive to the `contentIds` parameter as it iterates through content ids to remove
 *
 * @param  {Collection}     collection      The collection from which to remove the content items
 * @param  {String[]}       contentIds      The content item ids to remove from the collection
 * @param  {Function}       callback        Standard callback function
 * @param  {Object}         callback.err    An error that occurred, if any
 * @api private
 */
var _remove = function(collection, contentIds, callback) {
    if (_.isEmpty(contentIds)) {
        // When there are no more items to remove, return to the caller
        return callback();
    }

    // Remove the item from the library index
    var contentId = contentIds.pop();
    LibraryAPI.Index.remove(CollectionsConstants.library.CONTENT_LIBRARY_INDEX_NAME, [collection.groupId], contentItem.id, null, function(err) {
        if (err) {
            log().warn({
                'err': err,
                'indexName': CollectionsConstants.library.CONTENT_LIBRARY_INDEX_NAME,
                'collectionId': collection.id,
                'collectionGroupId': collection.groupId,
                'contentId': contentId
            }, 'An error occurred while removing a content item from a collection library');
        }

        // Recursively remove the next item
        return _remove(collection, contentIds, callback);
    });
};
