var util = require('util');

var AuthzUtil = require('oae-authz/lib/util');

/**
 * A model object that represents a collection
 *
 * @param  {Tenant}     tenant          The tenant to which the collection belongs
 * @param  {String}     id              The resource id of the collection
 * @param  {String}     groupId         The id of the authz group that this collection represents in the authz indexes
 * @param  {String}     createdBy       The id of the user who created the collection
 * @param  {String}     displayName     The display name of the collection
 * @param  {String}     description     The description of the collection
 * @param  {String}     visibility      The visibility of the collection
 * @param  {Number}     created         The timestamp (millis since epoch) that the collection was created
 * @param  {Number}     lastModified    The timestamp (millis since epoch) that the collection was last modified
 */
var Collection = module.exports.Collection = function(tenant, id, groupId, createdBy, displayName, description, visibility, created, lastModified) {
    var resourceId = AuthzUtil.getResourceFromId(id).resourceId;
    var that = {};
    that.tenant = tenant;
    that.id = id;
    that.groupId = groupId;
    that.createdBy = createdBy;
    that.displayName = displayName;
    that.description = description;
    that.visibility = visibility;
    that.created = created;
    that.lastModified = lastModified;
    that.profilePath = util.format('/collection/%s/%s', tenant.alias, resourceId);
    that.resourceType = 'collection';
    return that;
};
