var Cassandra = require('oae-util/lib/cassandra');

var api = require('./api');

/**
 * Ensure that the config schema is created.
 * If both the schema and the default config exist, then this method will do nothing.
 *
 * @param {Function} callback A function(err), specifying whether or not there was an error creating the schema.
 */
module.exports.ensureSchema = function(callback) {
    callback = callback || function() {};
    // Bootstrap the Config CF, if necessary
    Cassandra.createColumnFamily('Config', 'create columnfamily Config (tenantid text primary key)', callback);
};

/**
 * Drop the config schema and all of its data.
 *
 * @param {Function} callback A function(err), specifying whether or not there was an error dropping the schema
 */
module.exports.dropSchema = function(callback) {
    callback = callback || function() {};
    Cassandra.dropColumnFamily('Config', callback);
};