var TreeStructure = module.exports.TreeStructure = function(name, description, tree, options) {
    options = options || {};
    options.tenantOverride = options.tenantOverride || true;

    return {
        'description': description,
        'defaultValue': tree,
        'name': name,
        'tenantOverride': options.tenantOverride,
        'type': 'treestructure'
    };
};
