var _ = require('underscore');
var OAE = require('oae-util/lib/oae');

var ConfigAPI = require('oae-config');


//////////////////
// Global admin //
//////////////////

/*!
 * Global config schema
 */
OAE.globalAdminRouter.on('get', '/api/config/schema', function(req, res) {
    ConfigAPI.getSchema(req.ctx, function(err, schema) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, schema);
    });
});

/*!
 * Global admin config values
 */
OAE.globalAdminRouter.on('get', '/api/config', function(req, res) {
    ConfigAPI.getTenantConfig(req.ctx, req.ctx.tenant().alias, function(err, config) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, config);
    });
});

/*!
 * Tenant config values
 */
OAE.globalAdminRouter.on('get', '/api/config/:tenantAlias', function(req, res) {
    ConfigAPI.getTenantConfig(req.ctx, req.params.tenantAlias, function(err, config) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, config);
    });
});

/*!
 * Update global admin config value
 */
OAE.globalAdminRouter.on('post', '/api/config', function(req, res) {
    ConfigAPI.updateConfig(req.ctx, req.ctx.tenant().alias, req.body, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});

/*!
 * Update tenant config value
 */
OAE.globalAdminRouter.on('post', '/api/config/:tenantAlias', function(req, res) {
    ConfigAPI.updateConfig(req.ctx, req.params.tenantAlias, req.body, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});

/*!
 * Clear tenant config value
 */
OAE.globalAdminRouter.on('post', '/api/config/:tenantAlias/clear', function(req, res) {
    var configFields = req.body.configFields;
    if (!_.isArray(configFields)) {
        configFields = [ configFields ];
    }

    ConfigAPI.clearConfig(req.ctx, req.params.tenantAlias, configFields, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});

