var express = require('express');
var log = require('oae-logger').logger('config');
var configAPI = require('oae-config');

module.exports = function(tenant) {
    log().info('Initializing config rest endpoints for tenant %s', tenant.alias);

    // TODO: Remove when the UI is in place
    tenant.server.use(express.static(__dirname + "/../public"));

    tenant.server.get('/api/config', function(req, res, next) {
        configAPI.getConfig(tenant.alias, function(err, configuration) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(configuration);
        });
    });

    tenant.server.post('/api/config', function(req, res, next) {
        configAPI.writeConfig(req.ctx, tenant.alias, req.body, function(err) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200);
        });
    });
};
