var OAE = require('oae-util/lib/oae');

var ConfigAPI = require('oae-config');


//////////////////
// Global admin //
//////////////////

OAE.globalAdminServer.get('/api/config', function(req, res, next) {
    ConfigAPI.getConfig(req.ctx.tenant().alias, function(err, configuration) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, configuration);
    });
});

OAE.globalAdminServer.get('/api/config/:tenantId', function(req, res, next) {
    ConfigAPI.getConfig(req.params.tenantId, function(err, configuration) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, configuration);
    });
});

OAE.globalAdminServer.post('/api/config', function(req, res, next) {
    ConfigAPI.writeConfig(req.ctx, req.ctx.tenant().alias, req.body, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});

OAE.globalAdminServer.post('/api/config/:tenantId', function(req, res, next) {
    ConfigAPI.writeConfig(req.ctx, req.params.tenantId, req.body, function(err) {
        if (err) {
            return res.send(err.code, err.body);
        }
        res.send(200);
    });
});

/////////////
// Tenants //
/////////////

OAE.tenantServer.get('/api/config', function(req, res, next) {
    ConfigAPI.getConfig(req.ctx.tenant().alias, function(err, configuration) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(configuration);
    });
});

OAE.tenantServer.post('/api/config', function(req, res, next) {
    ConfigAPI.writeConfig(req.ctx, req.ctx.tenant().alias, req.body, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});

