var events = require('events');

var RestAPI = require('oae-rest');

var ConfigAPI = require('oae-config');

// Maintains the number of updates that have not yet been refreshed in the application cache
var currentUpdateCount = 0;

// Used to indicate when the configuration is up to date
var updateEmitter = new events.EventEmitter();

/**
 * Update the configuration as described by @see RestAPI.Config#updateConfig. In addition, this test utility will bind
 * and listen on an event that waits for the local configuration to be refreshed from cassandra. This is useful to
 * synchronize with the asynchronous nature of this operation so you can continue tests after everything is in sync.
 *
 * For method parameter descriptions, @see RestAPI.Config#updateConfig
 */
var updateConfigAndWait = module.exports.updateConfigAndWait = function(restCtx, tenantAlias, configUpdate, callback) {
    RestAPI.Config.updateConfig(restCtx, tenantAlias, configUpdate, function(err) {
        if (err) {
            return callback(err);
        }

        return _whenConfigUpdated(callback);
    });
};

/**
 * Clear the configuration as described by @see RestAPI.Config#clearConfig. In addition, this test utility will bind
 * and listen on an event that waits for the local configuration to be refreshed from cassandra. This is useful to
 * synchronize with the asynchronous nature of this operation so you can continue tests after everything is in sync.
 *
 * For method parameter descriptions, @see RestAPI.Config#clearConfig
 */
var clearConfigAndWait = module.exports.clearConfigAndWait = function(restCtx, tenantAlias, configFields, callback) {
    RestAPI.Config.clearConfig(restCtx, tenantAlias, configFields, function(err) {
        if (err) {
            return callback(err);
        }

        return _whenConfigUpdated(callback);
    });
};

/**
 * Invokes the provided function only when the server's configuration is completely up to date.
 *
 * @param  {Function}   callback    Invoked when the cached configuration is up to date
 * @api private
 */
var _whenConfigUpdated = function(callback) {
    if (currentUpdateCount === 0) {
        return callback();
    }

    return updateEmitter.once('updated', callback);
};

/**
 * Increment the number of config updates are currently in the process of updating
 */
var _incrementUpdateCount = function() {
    currentUpdateCount++;
};

/**
 * Decrement the number of config updates are currently in the process of updating. If the count
 * reaches 0 as a result of this decrement, the `updated` event will be fired to invoke functions
 * that are waiting for the server's config to be fully updated.
 */
var _decrementUpdateCount = function() {
    if (currentUpdateCount > 0) {
        currentUpdateCount--;
        if (currentUpdateCount === 0) {
            updateEmitter.emit('updated');
        }
    }
};

// Manage counts of updates and refreshes, to help synchronize configuration updates
ConfigAPI.on('preUpdate', _incrementUpdateCount);
ConfigAPI.on('preClear', _incrementUpdateCount);
ConfigAPI.on('update', _decrementUpdateCount);
