var BackendUtil = require('./util');

/**
 * This backend isn't actually able to to store or retrieve anything.
 * It's primary use is to "add" content that lives on a remote server
 * and link to it.
 */

//////////////////////
// Storage methods. //
//////////////////////

/**
 * @borrows Interface.store as Remote.store
 */
var store = module.exports.store = function(ctx, file, options, callback) {
    callback({'code': 501, 'msg': 'Not implemented'});
};

/**
 * @borrows Interface.get as Remote.get
 */
var get = module.exports.get = function(ctx, uri, callback) {
    callback({'code': 501, 'msg': 'Not implemented'});
};

/**
 * @borrows Interface.remove as Remote.remove
 */
var remove = module.exports.remove = function(ctx, uri, callback) {
    callback({'code': 501, 'msg': 'Not implemented'});
};

/**
 * @borrows Interface.getDownloadLink as Remote.getDownloadLink
 */
var getDownloadLink = module.exports.getDownloadLink = function(ctx, uri) {
    // The URI will look something like:
    //     remote:http://www.google.com
    //
    // We return an object that will redirect the user to the link.
    // We simply strip off the 'remote:' bit from the URI.
    return {'code':301, 'url': BackendUtil.splitUri(uri).location};
};
