var MQ = require('oae-util/lib/mq');
var PreviewProcessorAPI = require('oae-preview-processor');

var ContentAPI = require('./api');
var ContentConstants = require('oae-content/lib/constants').ContentConstants;

ContentAPI.on(ContentConstants.events.CREATED_CONTENT, function(ctx, content, members) {
    PreviewProcessorAPI.submitForProcessing(content.id);
});

ContentAPI.on(ContentConstants.events.UPDATED_CONTENT, function(ctx, newContentObj, oldContentObj) {
    // Only re-generate previews if the link changes.
    // New file bodies have their own event.
    if (newContentObj.resourceSubType === 'link' && newContentObj.link !== oldContentObj.link) {
        PreviewProcessorAPI.submitForProcessing(newContentObj.contentId);
    }
});

ContentAPI.on(ContentConstants.events.UPDATED_CONTENT_BODY, function(ctx, content, opts) {
    PreviewProcessorAPI.submitForProcessing(content.contentId);
});
