var events = require('events');

/**
 * ### Events
 *
 * The `DiscussionsAPI`, as enumerated in `DiscussionsConstants.events`, emits the following events:
 *
 * * `createdDiscussion(ctx, discussion)`: A new discussion was created.
 * * `updatedDiscussion(ctx, oldDiscussion, newDiscussion)`: An existing discussion's metadata has been updated.
 * * `deletedDiscussion(ctx, discussionId)`: A discussion has been deleted.
 * * `updatedDiscussionMembers(ctx, discussion, memberUpdates)`: The members and/or managers for a discussion have been altered.
 * * `createdDiscussionMessage(ctx, discussion, message)`: A new message was posted to a discussion
 * * `deletedDiscussionMessage(ctx, discussion, messageCreatedTimestamp, deleteType, message)`: A message was removed from a discussion.
 */
var DiscussionsAPI = module.exports = new events.EventEmitter();

module.exports.Discussions = require('./api.discussions');
module.exports.Authz = require('./api.authz');
