var Cassandra = require('oae-util/lib/cassandra');

module.exports = function(config, callback) {
    _ensureSchema(function(err) {
        if (err) {
            return callback(err);
        }

        // Register the activity producers, routers, transformers.
        require('./activity');

        // Register our search indexers, transformers, endpoints.
        require('./search');

        callback();
    });
};

/**
 * Ensure that the all of the discussion schemas are created. If they already exist, this method will not do anything
 *
 * @param  {Function}         callback       Standard callback function
 * @param  {Object}           callback.err   Error object, containing the error message
 * @api private
 */
var _ensureSchema = function(callback) {
    Cassandra.createColumnFamilies({
        'Discussions': 'CREATE COLUMNFAMILY Discussions (id text PRIMARY KEY, tenantAlias text, displayName text, visibility text, description text, createdBy text, created text, lastModified text);'
    }, callback);
};
