var _ = require('underscore');

var AuthzInvitationsDAO = require('oae-authz/lib/invitations/dao');
var AuthzUtil = require('oae-authz/lib/util');
var Context = require('oae-context').Context;
var Invitation = require('oae-authz/lib/invitations/model').Invitation;
var ResourceActions = require('oae-resource/lib/actions');
var ResourceConstants = require('oae-resource/lib/constants').ResourceConstants;

var DiscussionsAPI = require('oae-discussions');
var DiscussionsConstants = require('oae-discussions/lib/constants').DiscussionsConstants;
var DiscussionsDAO = require('oae-discussions/lib/internal/dao');

var log = require('oae-logger').logger('oae-discussions-invitations');

/*!
 * When an invitation is accepted, pass on the events to update discussion members and then feed
 * back the discussion resources into the event emitter
 */
ResourceActions.when(ResourceConstants.events.ACCEPTED_INVITATION, function(ctx, invitationHashes, memberChangeInfosByResourceId, inviterUsersById, callback) {
    // Filter the invitations and changes down to only discussion invitations
    var discussionIds = _.chain(memberChangeInfosByResourceId)
        .keys()
        .filter(_isDiscussionId)
        .value();
    if (_.isEmpty(discussionIds)) {
        return callback();
    }

    DiscussionsDAO.getDiscussionsById(discussionIds, null, function(err, discussions) {
        if (err) {
            log().warn({
                'err': err,
                'discussionIds': discussionIds
            }, 'An error occurred while getting discussions to update discussion libraries after an invitation was accepted');
            return callback();
        }

        // Invoke the "accept invitation" handler with the resources when we have them. We
        // invoke this after the get principals call for test synchronization
        callback(null, discussions);

        // Fire members update tasks for each discussion
        _.each(discussions, function(discussion) {
            var invitationHash = _.findWhere(invitationHashes, {'resourceId': discussion.id});
            var inviterUser = inviterUsersById[invitationHash.inviterUserId];

            var invitationCtx = Context.fromUser(inviterUser);
            var invitation = Invitation.fromHash(invitationHash, discussion, inviterUser);
            var memberChangeInfo = memberChangeInfosByResourceId[discussion.id];

            return DiscussionsAPI.emit(DiscussionsConstants.events.UPDATED_DISCUSSION_MEMBERS, invitationCtx, discussion, memberChangeInfo, {'invitation': invitation});
        });
    });
});

/*!
 * When a discussion is deleted, we delete all invitations associated to it
 */
DiscussionsAPI.when(DiscussionsConstants.events.DELETED_DISCUSSION, function(ctx, discussion, memberIds, callback) {
    AuthzInvitationsDAO.deleteInvitationsByResourceId(discussion.id, function(err) {
        if (err) {
            log().warn({
                'err': err,
                'discussionId': discussion.id
            }, 'An error occurred while removing invitations after a discussion was deleted');
        }

        return callback();
    });
});

/**
 * Determine if the given id is a discussion id
 *
 * @param  {String}     discussionId    The id to check
 * @return {Boolean}                    Whether or not the string was a discussion id
 * @api private
 */
var _isDiscussionId = function(discussionId) {
    return (AuthzUtil.isResourceId(discussionId) && discussionId.indexOf('d:') === 0);
};
