        /**
         * Test that verifies share permissions
         */
        it('verify discussion share permissions', function(callback) {
            DiscussionsTestsUtil.setupMultiTenantPrivacyEntities(function(publicTenant, publicTenant1, privateTenant, privateTenant1) {

                // 1. Verify anonymous user cannot share public discussion
                RestAPI.Discussions.shareDiscussion(publicTenant.anonymousRestContext, publicTenant.publicDiscussion.id, [publicTenant.publicUser.user.id], function(err) {
                    assert.ok(err);
                    assert.equal(err.code, 401);

                    // 2. Verify authenticated user cannot share private discussion
                    RestAPI.Discussions.shareDiscussion(publicTenant.publicUser.restContext, publicTenant.privateDiscussion.id, [publicTenant.loggedinUser.user.id], function(err) {
                        assert.ok(err);
                        assert.equal(err.code, 401);

                        // 3. Verify authenticated user can share loggedin discussion
                        RestAPI.Discussions.shareDiscussion(publicTenant.publicUser.restContext, publicTenant.loggedinDiscussion.id, [publicTenant.loggedinUser.user.id], function(err) {
                            assert.ok(!err);

                            // 3.1 Verify it went into loggedinUser's library
                            RestAPI.Discussions.getDiscussionsLibrary(publicTenant.loggedinUser.restContext, publicTenant.loggedinUser.user.id, null, null, function(err, items) {
                                assert.ok(!err);
                                assert.equal(items.results.length, 1);
                                _assertContainsItem(items.results, publicTenant.loggedinDiscussion.id);

                                // 3.2 Verify loggedin user from another tenant cannot see the library from a loggedin user from another tenant
                                RestAPI.Discussions.getDiscussionsLibrary(publicTenant1.loggedinUser.restContext, publicTenant.loggedinUser.user.id, null, null, function(err, items) {
                                    assert.equal(err.code, 401);

                                    // 4. Verify authenticated user can share loggedin discussion with public external tenant user
                                    RestAPI.Discussions.shareDiscussion(publicTenant.publicUser.restContext, publicTenant.loggedinDiscussion.id, [publicTenant1.publicUser.user.id], function(err) {
                                        assert.ok(!err);

                                        // 4.1 Verify it went into tenant1 publicUser's library
                                        RestAPI.Discussions.getDiscussionsLibrary(publicTenant1.publicUser.restContext, publicTenant1.publicUser.user.id, null, null, function(err, items) {
                                            assert.ok(!err);
                                            assert.equal(items.results.length, 1);
                                            _assertContainsItem(items.results, publicTenant.loggedinDiscussion.id);

                                            // 4.2 Verify a user from the external tenant (publicTenant1) cannot see the loggedin item in the shared user's library, because it is loggedin from another tenant
                                            RestAPI.Discussions.getDiscussionsLibrary(publicTenant1.loggedinUser.restContext, publicTenant1.publicUser.user.id, null, null, function(err, items) {
                                                assert.ok(!err);
                                                assert.equal(items.results.length, 0);

                                                // 5. Verify authenticated user cannot share loggedin discussion with private external tenant user
                                                RestAPI.Discussions.shareDiscussion(publicTenant.publicUser.restContext, publicTenant.loggedinDiscussion.id, [privateTenant.publicUser.user.id], function(err) {
                                                    assert.ok(err);
                                                    assert.equal(err.code, 400);

                                                    // 6. Verify authenticated user cannot share external loggedin discussion
                                                    RestAPI.Discussions.shareDiscussion(publicTenant.publicUser.restContext, publicTenant1.loggedinDiscussion.id, [publicTenant.loggedinUser.user.id], function(err) {
                                                        assert.ok(err);
                                                        assert.equal(err.code, 401);

                                                        // 7. Verify authenticated user can share external public discussion
                                                        RestAPI.Discussions.shareDiscussion(publicTenant.publicUser.restContext, publicTenant1.publicDiscussion.id, [publicTenant.loggedinUser.user.id], function(err) {
                                                            assert.ok(!err);

                                                            // 7.1 Verify it went into the user's library
                                                            RestAPI.Discussions.getDiscussionsLibrary(publicTenant.loggedinUser.restContext, publicTenant.loggedinUser.user.id, null, null, function(err, items) {
                                                                assert.ok(!err);
                                                                assert.equal(items.results.length, 2);
                                                                _assertContainsItem(items.results, publicTenant1.publicDiscussion.id);

                                                                // 7.2 Verify public user from the same tenant can see the public external item in the library -- because it is public.
                                                                RestAPI.Discussions.getDiscussionsLibrary(publicTenant.publicUser.restContext, publicTenant.loggedinUser.user.id, null, null, function(err, items) {
                                                                    assert.ok(!err);
                                                                    assert.equal(items.results.length, 2);
                                                                    _assertContainsItem(items.results, publicTenant.loggedinDiscussion.id);
                                                                    _assertContainsItem(items.results, publicTenant1.publicDiscussion.id);

                                                                    // 8. Verify authenticated user cannot share external public discussion with external public user from private tenant
                                                                    RestAPI.Discussions.shareDiscussion(publicTenant.publicUser.restContext, privateTenant1.publicDiscussion.id, [privateTenant1.publicUser.user.id], function(err) {
                                                                        assert.ok(err);
                                                                        assert.equal(err.code, 401);

                                                                        // 9. Verify authenticated user cannot share external public discussion from private tenant with user from their own tenant
                                                                        RestAPI.Discussions.shareDiscussion(publicTenant.publicUser.restContext, privateTenant1.publicDiscussion.id, [publicTenant.publicUser.user.id], function(err) {
                                                                            assert.ok(err);
                                                                            assert.equal(err.code, 401);
                                                                            return callback();
                                                                        });
                                                                    });
                                                                });
                                                            });
                                                        });
                                                    });
                                                });
                                            });
                                        });
                                    });
                                });
                            });
                        });
                    });
                });
            });
        });

