var _ = require('underscore');
var assert = require('assert');

var RestAPI = require('oae-rest');
var SearchTestsUtil = require('oae-search/lib/test/util');
var TestsUtil = require('oae-tests');

describe('Discussion Library Search', function() {

    // REST contexts we can use to do REST requests
    var anonymousRestContext = null;
    var camAdminRestContext = null;

    before(function(callback) {
        anonymousRestContext = TestsUtil.createTenantRestContext(global.oaeTests.tenants.cam.host);
        camAdminRestContext = TestsUtil.createTenantAdminRestContext(global.oaeTests.tenants.cam.host);
        callback();
    });

    describe('Library search', function() {
        /**
         * A test that verifies a discussion library can be searched through
         */
        it('verify searching through a discussion library', function(callback) {
            TestsUtil.generateTestUsers(camAdminRestContext, 1, function(err, users) {
                assert.ok(!err);
                var simong = _.values(users)[0];

                // Create 2 discussions
                var randomTextA = TestsUtil.generateRandomText(5);
                var randomTextB = TestsUtil.generateRandomText(5);
                RestAPI.Discussions.createDiscussion(simong.restContext, randomTextA, randomTextA, 'public', null, null, function(err, discussionA) {
                    assert.ok(!err);
                    RestAPI.Discussions.createDiscussion(simong.restContext, randomTextB, randomTextB, 'public', null, null, function(err, discussionB) {
                        assert.ok(!err);

                        // Only the A discussion should return
                        SearchTestsUtil.searchAll(simong.restContext, 'discussion-library', [simong.user.id], {'q': randomTextA}, function(err, results) {
                            assert.ok(!err);
                            assert.equal(results.total, 1);
                            assert.equal(results.results.length, 1);
                            var doc = _.find(results.results, function(result) { return result.id === discussionA.id; });
                            assert.ok(doc);
                            assert.equal(doc.displayName, randomTextA);
                            assert.equal(doc.description, randomTextA);
                            callback();
                        });
                    });
                });
            });
        });

        /**
         * Verifies that one can find discussions in his or her library based on message bodies.
         */
        it('verify searching through a discussion library searches through messages as well', function(callback) {
            TestsUtil.generateTestUsers(camAdminRestContext, 1, function(err, users) {
                assert.ok(!err);
                var simong = _.values(users)[0];

                var discussionTextA = TestsUtil.generateRandomText(5);
                var discussionTextB = TestsUtil.generateRandomText(5);
                RestAPI.Discussions.createDiscussion(simong.restContext, discussionTextA, discussionTextA, 'public', null, null, function(err, discussionA) {
                    assert.ok(!err);
                    RestAPI.Discussions.createDiscussion(simong.restContext, discussionTextB, discussionTextB, 'public', null, null, function(err, discussionB) {
                        assert.ok(!err);

                        // Add a message on discussion A
                        var msgText = TestsUtil.generateRandomText(5);
                        RestAPI.Discussions.createMessage(simong.restContext, discussionA.id, msgText, null, function(err, message) {
                            assert.ok(!err);

                            // When searching for the message, only discussion A should return
                            SearchTestsUtil.searchAll(simong.restContext, 'discussion-library', [simong.user.id], {'q': msgText}, function(err, results) {
                                assert.ok(!err);
                                assert.equal(results.total, 1);
                                assert.equal(results.results.length, 1);
                                var doc = _.find(results.results, function(result) { return result.id === discussionA.id; });
                                assert.ok(doc);
                                assert.equal(doc.displayName, discussionTextA);
                                assert.equal(doc.description, discussionTextA);
                                callback();
                            });
                        });
                    });
                });
            });
        });
    });
});