var _ = require('underscore');
var assert = require('assert');
var fs = require('fs');

var ActivityConstants = require('oae-activity/lib/constants').ActivityConstants;
var ActivityTestsUtil = require('oae-activity/lib/test/util');
var RestAPI = require('oae-rest');
var RestContext = require('oae-rest/lib/model').RestContext;
var TestsUtil = require('oae-tests');

var DiscussionsConstants = require('oae-discussions/lib/constants').DiscussionsConstants;

describe('Discussion Push', function() {
    // Rest contexts that can be used performing rest requests
    var localAdminRestContext = null;

    /**
     * Function that will fill up the tenant admin and anymous rest contexts
     */
    before(function(callback) {
        localAdminRestContext = TestsUtil.createTenantAdminRestContext(global.oaeTests.tenants.localhost.host);
        callback();
    });

    describe('Authorization', function() {

        /**
         * Test that verifies registering for a feed goes through the proper authorization checks
         */
        it('verify signatures must be valid', function(callback) {
            TestsUtil.generateTestUsers(localAdminRestContext, 2, function(err, users, simong, branden) {
                assert.ok(!err);

                RestAPI.User.getMe(simong.restContext, function(err, simonFull) {
                    assert.ok(!err);

                    var data = {
                        'authentication': {
                            'userId': simonFull.id,
                            'tenantAlias': simonFull.tenant.alias,
                            'signature': simonFull.signature
                        },
                        'feeds': []
                    };

                    ActivityTestsUtil.getFullySetupPushClient(data, function(client) {

                        // Create a discussion and get its full profile so we have a signature that we can use to register for push notifications
                        RestAPI.Discussions.createDiscussion(simong.restContext, 'displayName', 'description', 'public', [branden.user.id], null, function(err, discussion) {
                            assert.ok(!err);
                            RestAPI.Discussions.getDiscussion(simong.restContext, discussion.id, function(err, discussion) {
                                assert.ok(!err);

                                // Ensure we get a 400 error with an invalid activity stream id
                                client.subscribe(discussion.id, null, discussion.signature, null, function(err) {
                                    assert.equal(err.code, 400);

                                    // Ensure we get a 400 error with a missing resource id
                                    client.subscribe(null, 'activity', discussion.signature, null, function(err) {
                                        assert.equal(err.code, 400);

                                        // Ensure we get a 400 error with an invalid token
                                        client.subscribe(discussion.id, 'activity', {'signature': discussion.signature.signature}, null, function(err) {
                                            assert.equal(err.code, 401);
                                            client.subscribe(discussion.id, 'activity', {'expires': discussion.signature.expires}, null, function(err) {
                                                assert.equal(err.code, 401);

                                                // Ensure we get a 401 error with an incorrect signature
                                                client.subscribe(discussion.id, 'activity', {'expires': Date.now() + 10000, 'signature': 'foo'}, null, function(err) {
                                                    assert.equal(err.code, 401);

                                                    // Simon should not be able to use a signature that was generated for Branden
                                                    RestAPI.Discussions.getDiscussion(branden.restContext, discussion.id, function(err, discussionForBranden) {
                                                        assert.ok(!err);
                                                        client.subscribe(discussion.id, 'activity', discussionForBranden.signature, null, function(err) {
                                                            assert.equal(err.code, 401);

                                                            // Sanity check that a valid signature works
                                                            client.subscribe(discussion.id, 'activity', discussion.signature, null, function(err) {
                                                                assert.ok(!err, JSON.stringify(err));
                                                                return callback();
                                                            });
                                                        });
                                                    });
                                                });
                                            });
                                        });
                                    });
                                });
                            });
                        });
                    });
                });
            });
        });
    });

    describe('Notifications', function() {

        /**
         * Creates 2 users: `Branden` and `Simon` who are both managers of a discussion. A websocket will be created
         * for the `Simon`-user which is both authenticated and registered for push notifications on the discussion.
         *
         * @param  {Function}       callback                Gets executed once the fixture has been setup
         * @param  {Object}         callback.contexts       An object that holds the context and user info for the created users
         * @param  {Discussion}     callback.discussion     The created discussion
         * @param  {Client}         callback.client         A websocket client that is authenticated for the `Simon`-user and is registered for push notificates on the created discussion
         * @throws {Error}                                  If anything goes wrong, an assertion error will be thrown
         */
        var setupFixture = function(callback) {
            TestsUtil.generateTestUsers(localAdminRestContext, 2, function(err, users, branden, simon) {
                assert.ok(!err);

                var contexts = {
                    'branden': branden,
                    'simon': simon
                };

                // Get the full profile so we have a signature to authenticate ourselves on the WS
                RestAPI.User.getMe(contexts['simon'].restContext, function(err, simonFull) {
                    assert.ok(!err);

                    // Create a discussion and get the full discussion profile so we have a signature that we can use to register for push notifications
                    RestAPI.Discussions.createDiscussion(contexts['simon'].restContext, 'A file', 'A proper file', 'private', [contexts['branden'].user.id], [], function(err, discussion) {
                        assert.ok(!err);
                        RestAPI.Discussions.getDiscussion(contexts['simon'].restContext, discussion.id, function(err, discussion) {
                            assert.ok(!err);

                            // Route and deliver activities
                            ActivityTestsUtil.collectAndGetActivityStream(contexts['simon'].restContext, null, null, function() {

                                // Register for some streams
                                var data = {
                                    'authentication': {
                                        'userId': contexts['simon'].user.id,
                                        'tenantAlias': simonFull.tenant.alias,
                                        'signature': simonFull.signature
                                    },
                                    'streams': [
                                        {
                                            'resourceId': discussion.id,
                                            'streamType': 'activity',
                                            'token': discussion.signature
                                        },
                                        {
                                            'resourceId': discussion.id,
                                            'streamType': 'message',
                                            'token': discussion.signature
                                        }
                                    ]
                                };

                                ActivityTestsUtil.getFullySetupPushClient(data, function(client) {
                                    callback(contexts, discussion, client);
                                });
                            });
                        });
                    });
                });
            });
        };

        /**
         * Test that verifies an update gets pushed out
         */
        it('verify updates trigger a push notification', function(callback) {
            setupFixture(function(contexts, discussion, client) {

                // Trigger an update
                RestAPI.Discussions.updateDiscussion(contexts['branden'].restContext, discussion.id, {'displayName': 'Laaike whatevs'}, function(err) {
                    assert.ok(!err);
                });

                client.on('message', function(message) {
                    ActivityTestsUtil.assertActivity(message.activity, DiscussionsConstants.activity.ACTIVITY_DISCUSSION_UPDATE, ActivityConstants.verbs.UPDATE, contexts['branden'].user.id, discussion.id);

                    // Verify the updated display name is present on the activity object
                    assert.equal(message.activity.object.displayName, 'Laaike whatevs');

                    client.close(callback);
                });
            });
        });

        /**
         * Test that verifies a visibility update gets pushed out
         */
        it('verify visibility updates trigger a push notification', function(callback) {
            setupFixture(function(contexts, discussion, client) {

                // Trigger an update
                RestAPI.Discussions.updateDiscussion(contexts['branden'].restContext, discussion.id, {'visibility': 'loggedin'}, function(err) {
                    assert.ok(!err);
                });

                client.on('message', function(message) {
                    ActivityTestsUtil.assertActivity(message.activity, DiscussionsConstants.activity.ACTIVITY_DISCUSSION_UPDATE_VISIBILITY, ActivityConstants.verbs.UPDATE, contexts['branden'].user.id, discussion.id);

                    // Verify the updated visibility setting is present on the activity object
                    assert.equal(message.activity.object.visibility, 'loggedin');

                    client.close(callback);
                });
            });
        });

        /**
         * Test that verifies a new message gets pushed out
         */
        it('verify a new message triggers a push notification', function(callback) {
            setupFixture(function(contexts, discussion, client) {

                var discussionMessage = null;

                // Create a message
                RestAPI.Discussions.createMessage(contexts['branden'].restContext, discussion.id, 'Cup a Soup', null, function(err, _discussionMessage) {
                    assert.ok(!err);
                    discussionMessage = _discussionMessage;
                });

                client.on('message', function(message) {
                    ActivityTestsUtil.assertActivity(message.activity, DiscussionsConstants.activity.ACTIVITY_DISCUSSION_MESSAGE, ActivityConstants.verbs.POST, contexts['branden'].user.id, discussionMessage.id, discussion.id);

                    // Verify that we have access to the message body
                    assert.equal(message.activity.object.body, 'Cup a Soup');

                    // Verify the createdBy property is available
                    assert.ok(_.isObject(message.activity.object.createdBy));
                    assert.equal(message.activity.object.createdBy.id, contexts['branden'].user.id);

                    client.close(callback);
                });
            });
        });

        /**
         * Test that verifies a message author's profile gets scrubbed
         */
        it('verify a message author\'s profile gets scrubbed', function(callback) {
            setupFixture(function(contexts, discussion, client) {

                RestAPI.User.updateUser(contexts['branden'].restContext, contexts['branden'].user.id, {'visibility': 'private', 'publicAlias': 'Ma Baker'}, function(err) {
                    assert.ok(!err);

                    var discussionMessage = null;

                    // Create a message and listen for the activity's push notification
                    RestAPI.Discussions.createMessage(contexts['branden'].restContext, discussion.id, 'Cup a Soup', null, function(err, _discussionMessage) {
                        assert.ok(!err);
                        discussionMessage = _discussionMessage;
                    });

                    client.on('message', function(message) {
                        ActivityTestsUtil.assertActivity(message.activity, DiscussionsConstants.activity.ACTIVITY_DISCUSSION_MESSAGE, ActivityConstants.verbs.POST, contexts['branden'].user.id, discussionMessage.id, discussion.id);

                        // Verify that we have access to the message body
                        assert.equal(message.activity.object.body, 'Cup a Soup');
                        assert.equal(message.activity.object.createdBy.visibility, 'private');
                        assert.equal(message.activity.object.createdBy.displayName, 'Ma Baker');

                        client.close(callback);
                    });
                });
            });
        });
    });
});
