var express = require('express');
var fs = require('fs');

var DocAPI = require('./api');
var OAE = require('oae-util/lib/oae');

module.exports = function(tenant) {

    tenant.server.use(express.static(__dirname + "/../public"));

    /**
     * Get documentation for a module
     *
     * Rest Endpoint:
     *
     * _GET_ `/api/doc/module/[moduleId]`
     * * returns JSON representation of js in the module's `lib` directory
     *   formatted like
     *       { "api.js" : {output from dox}, "rest.js": {output from dox} }
     *   the dox JSON is documented at https://github.com/visionmedia/dox
     */
    tenant.server.get('/api/doc/module/:moduleId', function(req, res) {
        DocAPI.getDoc(req.params.moduleId, function(docs) {
            res.send(200, docs);
        });
    });

    /**
     * Get list of moduleIds
     *
     * Rest Endpoint:
     *
     * _GET_ `/api/doc/modules`
     * * returns JSON array of `moduleId`s for all OAE modules
     */
    tenant.server.get('/api/doc/modules', function(req, res) {
        OAE.getAvailableModules(function(data) {
            res.send(200, data);
        });
    });
};
