var OAE = require('oae-util/lib/oae');
var DocAPI = require('./api');

/**
 * Get documentation for a module
 *
 * Rest Endpoint:
 *
 * _GET_ `/api/doc/module/[moduleId]`
 *
 * * returns JSON representation of js in the module's `lib` directory
 *    formatted like
 *
 *         { "api.js" : {output from dox}, "rest.js": {output from dox} }
 *
 * The dox JSON is documented at https://github.com/visionmedia/dox
 */
OAE.tenantServer.get('/api/doc/:type/:name', function(req, res) {
    DocAPI.getDoc(req.params.name, req.params.type, function(err, docs) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, docs);
    });
});

/**
 * Get list of modules depending on the type
 *
 * Rest Endpoint:
 *
 * _GET_ `/api/doc/:type`
 *
 * * returns JSON array of backend or frontend modules
 */
OAE.tenantServer.get('/api/doc/:type', function(req, res) {
    if (req.params.type === 'frontend' || req.params.type === 'backend') {
        res.send(200, DocAPI.getDocumentationNames(req.params.type));
    } else {
        res.send(400, 'Unknown documentation type.');
    }
});
