var OAE = require('oae-util/lib/oae');
var Modules = require('oae-util/lib/modules');

var DocAPI = require('./api');


/**
 * Get documentation for a module
 *
 * Rest Endpoint:
 *
 * _GET_ `/api/doc/module/[moduleId]`
 *
 * * returns JSON representation of js in the module's `lib` directory
 *    formatted like
 *
 *         { "api.js" : {output from dox}, "rest.js": {output from dox} }
 *
 * The dox JSON is documented at https://github.com/visionmedia/dox
 */
OAE.tenantServer.get('/api/doc/module/:moduleId', function(req, res) {
    req.telemetryUrl = '/api/doc/module/id'
    DocAPI.getDoc(req.params.moduleId, function(err, docs) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, docs);
    });
});

/**
 * Get list of moduleIds
 *
 * Rest Endpoint:
 *
 * _GET_ `/api/doc/modules`
 *
 * * returns JSON array of `moduleId`s for all OAE modules
 */
OAE.tenantServer.get('/api/doc/modules', function(req, res) {
    req.telemetryUrl = '/api/doc/modules'
    res.send(200, Modules.getAvailableModules());
});

