var _ = require('underscore');
var assert = require('assert');
var fs = require('fs');

var ActivityConstants = require('oae-activity/lib/constants').ActivityConstants;
var ActivityTestsUtil = require('oae-activity/lib/test/util');
var RestAPI = require('oae-rest');
var RestContext = require('oae-rest/lib/model').RestContext;
var TestsUtil = require('oae-tests');

var FoldersConstants = require('oae-folders/lib/constants').FoldersConstants;
var FoldersTestUtil = require('oae-folders/lib/test/util');

describe('Folders - Push', function() {
    // Rest contexts that can be used performing rest requests
    var localAdminRestContext = null;

    /**
     * Function that will fill up the tenant admin and anymous rest contexts
     */
    before(function(callback) {
        localAdminRestContext = TestsUtil.createTenantAdminRestContext(global.oaeTests.tenants.localhost.host);
        callback();
    });

    describe('Authorization', function() {

        /**
         * Test that verifies registering for a feed goes through the proper authorization checks
         */
        it('verify signatures must be valid', function(callback) {
            TestsUtil.generateTestUsers(localAdminRestContext, 2, function(err, users, simong, branden) {
                assert.ok(!err);

                RestAPI.User.getMe(simong.restContext, function(err, simonFull) {
                    assert.ok(!err);

                    var data = {
                        'authentication': {
                            'userId': simonFull.id,
                            'tenantAlias': simonFull.tenant.alias,
                            'signature': simonFull.signature
                        },
                        'feeds': []
                    };

                    ActivityTestsUtil.getFullySetupPushClient(data, function(client) {

                        // Create a folder and get its full profile so we have a signature that we can use to register for push notifications
                        FoldersTestUtil.assertCreateFolderSucceeds(simong.restContext, 'test displayName', 'test description', 'private', [branden], [], function(folder) {
                            FoldersTestUtil.assertGetFolderSucceeds(simong.restContext, folder.id, function(folder) {

                                // Ensure we get a 400 error with an invalid activity stream id
                                client.subscribe(folder.id, null, folder.signature, null, function(err) {
                                    assert.equal(err.code, 400);

                                    // Ensure we get a 400 error with a missing resource id
                                    client.subscribe(null, 'activity', folder.signature, null, function(err) {
                                        assert.equal(err.code, 400);

                                        // Ensure we get a 400 error with an invalid token
                                        client.subscribe(folder.id, 'activity', {'signature': folder.signature.signature}, null, function(err) {
                                            assert.equal(err.code, 401);
                                            client.subscribe(folder.id, 'activity', {'expires': folder.signature.expires}, null, function(err) {
                                                assert.equal(err.code, 401);

                                                // Ensure we get a 401 error with an incorrect signature
                                                client.subscribe(folder.id, 'activity', {'expires': Date.now() + 10000, 'signature': 'foo'}, null, function(err) {
                                                    assert.equal(err.code, 401);

                                                    // Simon should not be able to use a signature that was generated for Branden
                                                    FoldersTestUtil.assertGetFolderSucceeds(branden.restContext, folder.id, function(folderForBranden) {
                                                        client.subscribe(folder.id, 'activity', folderForBranden.signature, null, function(err) {
                                                            assert.equal(err.code, 401);

                                                            // Sanity check that a valid signature works
                                                            client.subscribe(folder.id, 'activity', folder.signature, null, function(err) {
                                                                assert.ok(!err, JSON.stringify(err));
                                                                return callback();
                                                            });
                                                        });
                                                    });
                                                });
                                            });
                                        });
                                    });
                                });
                            });
                        });
                    });
                });
            });
        });
    });

    describe('Notifications', function() {

        /**
         * Creates 2 users: `Branden` and `Simon` who are both managers of a folder. A websocket will be created
         * for the `Simon`-user which is both authenticated and registered for push notifications on the folder.
         *
         * @param  {Function}       callback                Gets executed once the fixture has been setup
         * @param  {Object}         callback.contexts       An object that holds the context and user info for the created users
         * @param  {Folder}         callback.folder         The created folder
         * @param  {Client}         callback.client         A websocket client that is authenticated for the `Simon`-user and is registered for push notificates on the created folder
         * @throws {Error}                                  If anything goes wrong, an assertion error will be thrown
         */
        var setupFixture = function(callback) {
            TestsUtil.generateTestUsers(localAdminRestContext, 2, function(err, users, branden, simon) {
                assert.ok(!err);

                var contexts = {
                    'branden': branden,
                    'simon': simon
                };

                // Get the full profile so we have a signature to authenticate ourselves on the WS
                RestAPI.User.getMe(simon.restContext, function(err, simonFull) {
                    assert.ok(!err);

                    // Create a folder and get the full folder profile so we have a signature that we can use to register for push notifications
                    FoldersTestUtil.assertCreateFolderSucceeds(simon.restContext, 'test displayName', 'test description', 'private', [branden], [], function(folder) {
                        FoldersTestUtil.assertGetFolderSucceeds(simon.restContext, folder.id, function(folder) {

                            // Route and deliver activities
                            ActivityTestsUtil.collectAndGetActivityStream(simon.restContext, null, null, function() {

                                // Register for some streams
                                var data = {
                                    'authentication': {
                                        'userId': simon.user.id,
                                        'tenantAlias': simonFull.tenant.alias,
                                        'signature': simonFull.signature
                                    },
                                    'streams': [
                                        {
                                            'resourceId': folder.id,
                                            'streamType': 'activity',
                                            'token': folder.signature
                                        }
                                    ]
                                };

                                ActivityTestsUtil.getFullySetupPushClient(data, function(client) {
                                    callback(contexts, folder, client);
                                });
                            });
                        });
                    });
                });
            });
        };

        /**
         * Test that verifies an update gets pushed out
         */
        it('verify updates trigger a push notification', function(callback) {
            setupFixture(function(contexts, folder, client) {

                // Trigger an update
                RestAPI.Folders.updateFolder(contexts['branden'].restContext, folder.id, {'displayName': 'Laaike whatevs'}, function(err, data) {
                    assert.ok(!err);
                });

                client.on('message', function(message) {
                    ActivityTestsUtil.assertActivity(message.activity, FoldersConstants.activity.ACTIVITY_FOLDER_UPDATE, ActivityConstants.verbs.UPDATE, contexts['branden'].user.id, folder.id);

                    // Verify the updated display name is present on the activity object
                    assert.equal(message.activity.object.displayName, 'Laaike whatevs');

                    client.close(callback);
                });
            });
        });

        /**
         * Test that verifies a visibility update gets pushed out
         */
        it('verify visibility updates trigger a push notification', function(callback) {
            setupFixture(function(contexts, folder, client) {

                // Trigger an update
                RestAPI.Folders.updateFolder(contexts['branden'].restContext, folder.id, {'visibility': 'loggedin', 'applyVisibilityOn': 'folder'}, function(err) {
                    assert.ok(!err);
                });

                client.on('message', function(message) {
                    ActivityTestsUtil.assertActivity(message.activity, FoldersConstants.activity.ACTIVITY_FOLDER_UPDATE_VISIBILITY, ActivityConstants.verbs.UPDATE, contexts['branden'].user.id, folder.id);

                    // Verify the updated visibility setting is present on the activity object
                    assert.equal(message.activity.object['oae:visibility'], 'loggedin');

                    client.close(callback);
                });
            });
        });

        /**
         * Test that verifies add-to-folder activities do not get pushed out via push notifications
         */
        it('verify add-to-folder activities do not trigger a push notification', function(callback) {
            setupFixture(function(contexts, folder, client) {

                // Add an item to a folder
                RestAPI.Content.createLink(contexts['simon'].restContext, 'test', 'test', 'public', 'http://www.google.ca', null, null, [folder.id], function(err, nicosLink) {
                    assert.ok(!err);
                });

                client.on('message', function(message) {
                    assert.fail('This activity should not have been routed to the folder');
                });

                // This timeout is not ideal, but we need to give the activity router some time
                // to let it do its thing
                setTimeout(function() {
                    client.close(callback);
                }, 300);
            });
        });
    });
});
