var OAE = require('oae-util/lib/OAE');

OAE.runQuery("create table Principals(principal_id text PRIMARY KEY, tenant text, privacy_setting text, alias text, user_institution_user_id text, user_first_name text, user_last_name text, user_email text, user_picture text, group_title text, group_description text, group_membership_type ascii) WITH comment='Principal information' ;");
OAE.runQuery("create table GroupMembers (group_id text PRIMARY KEY) WITH comment='Holds all the members for a group in one row';");
OAE.runQuery("create table MemberOf (principal_id text PRIMARY KEY) WITH comment='Holds all the groups that a principal is a (in)direct member of.';")