var log = require('oae-logger').logger('oae-mediacore');

var MediaCoreProcessor = require('./processor');

var Cassandra = require('oae-util/lib/cassandra');
var PreviewProcessorAPI = require('oae-preview-processor');

/**
 * Starts listening for new pieces of content that should be handled.
 */
module.exports = function(config, callback) {
    PreviewProcessorAPI.registerProcessor('oae-file-mediacore', MediaCoreProcessor);
    _ensureSchema(callback);
};

var _ensureSchema = function(callback) {
    Cassandra.createColumnFamilies({
        'Mediacore': 'CREATE COLUMNFAMILY Mediacore (mediacoreId text PRIMARY KEY);'
    }, callback);
};
