var Cassandra = require('oae-util/lib/cassandra');

var getId = module.exports.getId = function(mediacoreId, callback) {
    Cassandra.runQuery('SELECT * FROM Mediacore USING CONSISTENCY QUORUM WHERE mediacoreId = ?', [mediacoreId], function(err, rows) {
        if (err) {
            return callback(err);
        }

        return callback(null, Cassandra.rowToHash(rows[0]));
    });
};

var saveId = module.exports.saveId = function(mediacoreId, contentId, revisionId, callback) {
    var data = {
        'contentId': contentId,
        'revisionId': revisionId
    };
    var q = Cassandra.constructUpsertCQL('Mediacore', 'mediacoreId', mediacoreId, data, 'QUORUM');
    return Cassandra.runQuery(q.query, q.parameters, callback);
};
