var OAE = require('oae-util/lib/oae');

var MediaCoreAPI = require('./api');

/*!
 * Get the mediacore embed url from a content id
 */
OAE.tenantServer.get('/api/mediacore/embed/:id', function(req, res) {
    req.telemetryUrl = '/api/mediacore/embed/id';

    MediaCoreAPI.getEmbedCode(req.ctx, req.params.id, function(err, embedCode) {

        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(200, embedCode);
    });
});

/*!
 * Update the thumbnails from mediacore
 */
OAE.tenantServer.post('/api/mediacore/thumbs', function(req, res) {
    req.telemetryUrl = '/api/mediacore/thumbs';

    var mediacoreId = req.headers['media-id'];
    MediaCoreAPI.updateThumbs(mediacoreId, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(200);
    });
});
