var MessageBoxConstants = module.exports.MessageBoxConstants = {};

/*!
 * @see DiscussionAPI.deleteMessage for what these types mean.
 */
MessageBoxConstants.deleteTypes = {
    'SOFT': 'soft',
    'HARD': 'hard',
    'LEAF': 'leaf'
};

/*!
 * Events that get emitted by the messagebox API.
 */
MessageBoxConstants.events = {
    'CREATED_MESSAGE': 'createdMessage',
    'UPDATED_MESSAGE': 'updatedMessage',
    'DELETED_MESSAGE': 'deletedMessage'
};

/*!
 * Constants that get used in activity entities
 */
MessageBoxConstants.activity = {
    'PROP_OAE_MESSAGE_BOX_ID': 'oae:messageBoxId',
    'PROP_OAE_THREAD_KEY': 'oae:threadKey',
    'IN_REPLY_TO': 'inReplyTo'
};
