var _ = require('underscore');

var ActivityAPI = require('oae-activity');
var ActivityConstants = require('oae-activity/lib/constants').ActivityConstants;
var ActivityUtil = require('oae-activity/lib/util');

var MixpanelUtil = require('oae-mixpanel/lib/util');

module.exports = function(client, config) {
  /*!
     * An activity stream is retrieved
     */
  MixpanelUtil.listen(
    ActivityAPI,
    ActivityConstants.events.GET_ACTIVITY_STREAM,
    function(ctx, activityStreamId, start, limit, transformerType) {
      var params = MixpanelUtil.getBasicParameters(ctx);

      params.streamType = ActivityUtil.parseActivityStreamId(
        activityStreamId,
      ).streamType;
      params.transformerType = transformerType;
      params.start = parseInt(start, 10) || 0;

      client.track(ActivityConstants.events.GET_ACTIVITY_STREAM, params);
      client.people.increment(
        params.distinct_id,
        ActivityConstants.events.GET_ACTIVITY_STREAM,
      );
    },
  );
};
