var _ = require('underscore');
var querystring = require('querystring');
var request = require('request');
var urlAPI = require('url');

var IO = require('oae-util/lib/io');
var log = require('oae-logger').logger('oae-preview-processor');
var RestAPI = require('oae-rest');


/**
 * Whether or not this retriever can handle the passed in URL.
 *
 * @param  {String}     url     The URL to generate images for.
 * @return {Boolean}            true if this retriever can handle the URL, false otherwise.
 */
var test = module.exports.test = function(url) {
    return (_getId(url) !== null);
};


/**
 * Get an image for a URL.
 *
 * @param  {PreviewContext}     ctx             The preview context associated to this file.
 * @param  {String}             url             The URL to generate images for.
 * @param  {Function}           callback        Standard callback method.
 * @param  {Object}             callback.err    Standard error object (if any.)
 * @param  {String}             callback.path   The path on disk where the image is stored.
 * @param  {Object}             [callback.opts] Extra options that should be posted too the main content object. ex: displayName, description, ..
 */
var getImage = module.exports.getImage = function(ctx, url, callback) {
    var id = _getId(url);

    // Do an API request first.
    request('http://vimeo.com/api/v2/video/' + id + '.json', function(err, response, body) {
        if (err || response.statusCode !== 200) {
            return callback(err || {'code': response.statusCode, 'msg': body});
        }

        // Get Thumbnail url.
        var info = JSON.parse(body);
        var opts = {
            'displayName': info[0].title,
            'description': info[0].description,
            'imageUrl': info[0].thumbnail_medium
        };

        // Download it.
        ctx.downloadRemoteFile(opts.imageUrl, function(err, path) {
            if (err) {
                return callback(err);
            }

            callback(null, path, opts);
        });
    });
};

/**
 * Gets a Vimeo movie identifier out of a url.
 * If the url is 'http://vimeo.com/46651666', '46651666' will be returned.
 *
 * @param  {String} url The Vimeo URL.
 * @return {String}     The movie identifier (or null.)
 * @api private
 */
var _getId = function(url) {
    try {
        var parsedUrl = urlAPI.parse(url);
        if (!/vimeo\.com/.test(parsedUrl.hostname)) {
            return null;
        }
        return parsedUrl.path.substr(1);
    } catch (err) {
        return null;
    }
};
