var assert = require('assert');

var MQ = require('oae-util/lib/mq');

var PreviewConstants = require('oae-preview-processor/lib/constants');

/**
 * Purges all the events out of the previews queue
 *
 * @param  {Function}       callback        Standard callback function
 * @throws {AssertionError}                 Thrown if an error occurs while purging the queue
 */
var purgePreviewsQueue = module.exports.purgePreviewsQueue = function(callback) {
    // Bind a listener to the PP queue so that it may be purged
    MQ.bind(PreviewConstants.MQ.TASK_GENERATE_PREVIEWS, function() {}, {'subscribe': {'subscribe': false}}, function(err) {
        assert.ok(!err);

        // Purge anything that is in the queue
        MQ.purge(PreviewConstants.MQ.TASK_GENERATE_PREVIEWS, function(err) {
            assert.ok(!err);

            // Unbind our dummy-handler from the queue
            MQ.unbind(PreviewConstants.MQ.TASK_GENERATE_PREVIEWS, function(err) {
                assert.ok(!err);

                return callback();
            });
        });
    });
};
