var Time = require('time');

var log = require('oae-logger').logger('oae-principals');
var Cassandra = require('oae-util/lib/cassandra');
var Tenant = require('oae-tenants/lib/model').Tenant;
var Context = require('oae-context').Context;
var PrincipalsAPI = require('oae-principals');
var User = require('oae-principals/lib/model').User;
var LoginId = require('oae-principals/lib/model').LoginId;

module.exports = function(app, callback) {
    ensureSchema(function(err) {
        if (err) {
            return callback(err);
        }

        // We'll create/get 1 global admin account in the internal global tenant that can be used
        // to create tenants/other global accounts.
        var globalTenant = new Tenant('global', 'Global tenant', 2000, 'localhost');
        var globalUser = new User(globalTenant.alias, null, 'private', 'en_GB', 'Europe/London', 'Global Admin', true, false);
        var globalLoginId = new LoginId('local', globalTenant.alias, 'administrator');
        var globalContext = new Context(globalTenant, globalUser);

        // Create a global admin if it doesn't exist.
        var opts = {
          visibility: globalUser.visibility,
          locale: globalUser.locale,
          timezone: globalUser.timezone
        }; 
        
        PrincipalsAPI.createUser(globalContext, globalLoginId, 'administrator', globalUser.displayName, opts, function(err, userId) {
            // If no error occurred, we're starting with a clean keyspace.
            // We'll set the newly created user as a global admin.
            if (!err) {
                globalUser.id = userId;
                globalContext = new Context(globalTenant, globalUser);
                PrincipalsAPI.setGlobalAdmin(globalContext, userId, true, function(err) {
                    if (err) {
                        return callback(err);
                    }
                    log().info("Admin account with user id '%s' created.", userId);
                    callback();
                });
            } else if (err && err.msg && err.msg.indexOf('already exists') > 0) {
                // The user already exists so this is a reboot of an existing dataset.
                callback();
            } else {
                // This is an actual error!
                callback(err);
            }
        });
    });
}

/**
 * Ensure that the all of the principal-related schemas are created. If they already exist, this method will not
 * do anything
 * @param {Function(err)}    callback       A function(err), specifying whether or not there was an error creating the schema.
 * @param {Object}           callback.err   Error object, containing the error message
 */
var ensureSchema = function(callback) {
    // Both user and group information will be stored inside of the Principals CF
    Cassandra.createColumnFamilies({
        'Principals': "CREATE COLUMNFAMILY Principals (principalId text PRIMARY KEY, loginId text, alias text, tenant text, visibility text) WITH comment='Principal information';",
        'PrincipalsLoginId': "CREATE COLUMNFAMILY PrincipalsLoginId (loginId text PRIMARY KEY, userId text) WITH comment = 'Holds user login ID information. The row is prefixed by the authentication mechanism to which the ID belongs, then the tenant. The post-fix is the actual login id.'"
    }, callback);
};
