var util = require('util');

/**
 * The Group model.
 * If this group has a profile picture associated to it, this object will have the following keys:
 * `group.smallPicture`      : A download link to the small picture
 * `group.smallPictureUri`   : A content storage URI to the small picture.
 * `group.mediumPicture`     : A download link to the medium picture
 * `group.mediumPictureUri`  : A content storage URI to the medium picture.
 * `group.largePicture`      : A download link to the large picture
 * `group.largePictureUri`   : A content storage URI to the large picture.
 *
 * @param  {String}     tenant                  The tenant this group belongs to.
 * @param  {String}     id                      The globally unique principalId for this group. ie: g:cam:math-101
 * @param  {String}     alias                   The alias of this group
 * @param  {String}     [opts]                  Optional additional group properties
 * @param  {String}     [opts.visibility]       The visibility of this group. ie: loggedin
 * @param  {String}     [opts.joinable]         How this group can be joined
 * @param  {String}     [opts.displayName]      The name of the group
 * @param  {String}     [opts.description]      The description of the group
 */
module.exports.Group = function(tenant, id, alias, opts) {
    opts = opts || {};

    var that = {};
    that.id = id;
    that.alias = alias;
    that.tenant = tenant;
    that.visibility = opts.visibility;
    that.joinable = opts.joinable;
    that.displayName = opts.displayName;
    that.description = opts.description;
    that.profilePath = '/group/' + that.id;
    that.resourceType = 'group';

    return that;
};
