var principalAPI = require('./api');

module.exports = function(tenant) {

    //////////////
    //  USERS   //
    //////////////


    tenant.server.post('/api/users/create', function(req, res) {
        principalAPI.createUser(tenant, req.body.username, req.body.password, req.body.privacy, req.body.firstName, req.body.lastName, req.body.displayName, function(err, user) {
            if (err) {
                return res.send(err.code, err.msg);
            }

            res.send(201, user);
        });
    });

    tenant.server.get('/api/users/:id', function(req, res) {
        principalAPI.getUser(req.params.id, function(err, user) {
            if (err) {
                return res.send(err.code, err.msg);
            }

            res.send(200, user);
        });
    });

    tenant.server.post('/api/users/:id', function(req, res) {
        principalAPI.updateUser(req.body.user_id, req.body.privacy, req.body.firstName, req.body.lastName, req.body.displayName, function(err) {
            if (err) {
                return res.send(err.code, err.msg);
            }

            res.send(200);
        });
    });

    tenant.server.get('/api/users/:id/membership', function(req, res) {
        var limit = parseInt(req.query.limit || 100, 10);
        principalAPI.memberOf(req.params.id, true, req.query.start, limit, function(err, user) {
            if (err) {
                return res.send(err.code, err.msg);
            }

            res.send(200, user);
        });
    });


    ///////////////
    //  GROUPS   //
    ///////////////

    tenant.server.post('/api/groups/create', function(req, res) {
        principalAPI.createGroup(tenant, req.body.title, req.body.description, function(err, id) {
            if (err) {
                return res.send(err.code, err.msg);
            }

            res.send(201, {'id': id});
        });
    });

    tenant.server.get('/api/groups/:id', function(req, res) {
        principalAPI.getGroup(req.params.id, function(err, group) {
            if (err) {
                return res.send(err.code, err.msg);
            }

            var limit = parseInt(req.query.limit || 100, 10);
            api.getGroupMembers(req.params.id, true, req.query.start, limit, function(err, members) {
                if (err) {
                    return res.send(err.code, err.msg);
                }

                group.members = members;
                res.send(group);
            });
        });
    });

    tenant.server.get('/api/groups/:id/members', function(req, res) {
        var limit = parseInt(req.query.limit || 100, 10);
        userAPI.getGroupMembers(req.params.id, true, req.query.start, limit, function(err, members) {
            if (err) {
                return res.send(err.code, err.msg);
            }

            res.send(members);
        });
    });

    tenant.server.post('/api/groups/:id/add', function(req, res) {
        if (!req.query.principals) {
            return res.send(400, "No principals were specified.");
        }

        principalAPI.addGroupMembers(req.params.id, req.body.principals, function(err, members) {
            if (err) {
                return res.send(err.code, err.msg);
            }

            res.send(200);
        });
    });
};