var reCaptcha = require('recaptcha-async').reCaptcha;

var OAE = require('oae-util/lib/oae');
var PrincipalsConfig = require('oae-config').config('oae-principals');

var PrincipalsAPI = require('./api');


//////////////////
// Global admin //
//////////////////

OAE.globalAdminServer.get('/api/me', function(req, res) {
    PrincipalsAPI.getMe(req.ctx, function(err, meData) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, meData);
    });
});

/////////////
// Tenants //
/////////////

OAE.tenantServer.get('/api/me', function(req, res) {
    PrincipalsAPI.getMe(req.ctx, function(err, meData) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, meData);
    });
});

OAE.tenantServer.post('/api/user/create', function(req, res) {
    var createUser = function() {
        PrincipalsAPI.createUser(req.ctx, req.body.username, req.body.password, req.body.visibility, req.body.locale, req.body.timezone, req.body.firstName, req.body.lastName, req.body.displayName, function(err, user) {
            if (err) {
                return res.send(err.code, err.msg);
            }

            res.send(201, user);
        });
    };

    if (req.ctx.user()) {
        if (req.ctx.user().isAdmin(req.ctx.tenant().alias)) {
            // If the current user is an admin,
            // don't bother with reCaptcha.
            return createUser();
        } else {
            // Non-admin users cannot create accounts.
            return res.send(401);
        }
    }

    // Check if we need to validate with reCaptcha.
    var needsReCaptcha = PrincipalsConfig.getValue(req.ctx.tenant().alias, 'recaptcha', 'enabled');

    if (!needsReCaptcha) {
        return createUser();
    }

    // An anonymous user, do the recaptcha check.
    var recaptcha = new reCaptcha();
    recaptcha.on('data', function (reCaptchaResponse) {
        if (reCaptchaResponse.is_valid) {
            createUser();
        } else {
            return res.send(400, {'msg': 'Invalid reCaptcha token.'});
        }
    });

    recaptcha.checkAnswer(PrincipalsConfig.getValue(req.ctx.tenant().alias, 'recaptcha', 'private-key'), req.connection.remoteAddress, req.body.recaptchaChallenge, req.body.recaptchaResponse);
});

OAE.tenantServer.get('/api/user/:id', function(req, res) {
    req.telemetryUrl = '/api/user/id';
    PrincipalsAPI.getBasicProfile(req.ctx, req.params.id, function(err, profile) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        res.send(200, profile);
    });
});

OAE.tenantServer.post('/api/user/:id', function(req, res) {
    req.telemetryUrl = '/api/user/id';
    PrincipalsAPI.updateUser(req.ctx, req.params.id, req.body, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        res.send(200, '');
    });
});

OAE.tenantServer.get('/api/user/:id/memberships', function(req, res) {
    req.telemetryUrl = '/api/user/id/memberships';
    var limit = parseInt(req.query.limit || 10, 10);
    PrincipalsAPI.memberOf(req.ctx, req.params.id, req.query.start, limit, function(err, memberships) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        res.send(200, {'results': memberships});
    });
});

OAE.tenantServer.post('/api/user/:id/password', function(req, res) {
    req.telemetryUrl = '/api/user/id/passwords';
    PrincipalsAPI.changePassword(req.ctx, req.params.id, req.body.oldPassword, req.body.newPassword, function(err, changed) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        res.send(200);
    });
});

