var _ = require('underscore');
var AuthzAPI = require('oae-authz');
var AuthzUtil = require('oae-authz/lib/util');
var SearchConstants = require('oae-search/lib/constants').SearchConstants;
var SearchUtil = require('oae-search/lib/util');
var Validator = require('oae-authz/lib/validator').Validator;

var PrincipalsAPI = require('oae-principals');
var PrincipalsUtil = require('oae-principals/lib/util');

/**
 * Search that searches a group's members list
 *
 * @param   {Context}       ctx                 The context of the current request
 * @param   {Object}        opts                General search options
 * @param   {Array}         opts.params         An array of required parameters for the search
 * @param   {String}        opts.params[0]      The resource whose members to search (currently only groups are supported for members search)
 * @param   {Function}      callback            Invoked when the process completes
 * @param   {Object}        callback.err        An error that occurred, if any
 * @param   {SearchResult}  callback.results    An object that represents the results of the query
 */
module.exports = function(ctx, opts, callback) {
    // Sanitize the search options
    opts = opts || {};
    opts = {
        'principalId': opts.params[0],
        'q': SearchUtil.getQueryParam(opts.q),
        'limit': isNaN(opts.limit) ? 10 : opts.limit,
        'start': isNaN(opts.start) ? 0 : opts.start,
        'sort': SearchUtil.getSortParam(opts.sort)
    }

    var validator = new Validator();
    validator.check(opts.principalId, {'code': 400, 'msg': 'Must specificy an id of a principal to search its memberships'}).isPrincipalId();
    if (validator.hasErrors()) {
        return callback(validator.getFirstError());
    }

    _search(ctx, opts, callback);
};

/**
 * Perform the search that searches a user's memberships for content.
 *
 * @param   {Context}       ctx                 The context of the current request
 * @param   {Object}        opts                General search options
 * @param   {Function}      callback            Invoked when the process completes
 * @param   {Object}        callback.err        An error that occurred, if any
 * @param   {SearchResult}  callback.results    An object that represents the results of the query
 */
var _search = function(ctx, opts, callback) {

    // The query object for the Query DSL
    var query = SearchUtil.createQueryStringQuery(opts.q);

    AuthzAPI.getPrincipalMemberships(opts.principalId, null, 10000, function(err, memberships) {
        if (err) {
            return callback(err);
        }

        if (!memberships || memberships.length === 0) {
            return callback();
        }

        var filter = SearchUtil.filterAnd(
            SearchUtil.filterTerm('_type', SearchConstants.resourceMappingName),
            SearchUtil.filterTerm('resourceType', 'group'),
            SearchUtil.filterIds(memberships)
        );

        // Wrap the query and filter into the top-level Query DSL "query" object and return it
        callback(null, SearchUtil.createQuery(query, filter, opts));
    });
};
