var Cassandra = require('oae-util/lib/cassandra');

module.exports = function(app, callback) {
    ensureSchema(callback);
};

/**
 * Ensures that the Profiles schema is active. If it has already been created, this will have no effect.
 *
 * @param  {Function} callback A function that specifies whether or not the operation was successful
 * @param  {Object}   callback.err An error that occured, if any
 */
var ensureSchema = function(callback) {
    Cassandra.createColumnFamily('Profiles', 'CREATE COLUMNFAMILY Profiles (principalId text PRIMARY KEY)', callback);
};
