module.exports.Constants = {
    'visibility': {
        'PUBLIC': 'public',
        'LOGGEDIN': 'loggedin',
        'PRIVATE': 'private'
    }
};

/**
 * A profile section.
 *
 * @param  {String}  principalId     The id of the principal to whom this section belongs.
 * @param  {String}  name            The name of this section.
 * @param  {Object}  data            The data in this profile section.
 * @param  {String}  visibility      A visibility setting. One of 'public', 'loggedin', 'private'
 */
module.exports.ProfileSection = function(principalId, name, data, visibility) {
    var that = {};
    that.principalId = principalId;
    that.name = name;
    that.data = data;
    that.visibility = visibility;
    return that;
};
