var _ = require('underscore');

var OAE = require('oae-util/lib/oae');
var PrincipalsAPI = require('oae-principals');

var ProfileAPI = require('./api');


// Get all sections.
OAE.tenantRouter.on('get', '/api/user/:id/profile/sections', function(req, res) {
    PrincipalsAPI.getUser(req.ctx, req.params.id, function(err, user) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        ProfileAPI.getSectionOverview(req.ctx, req.params.id, function(err, sections) {
            if (err) {
                return res.send(err.code, err.msg);
            }

            var visibilities = {
                'sections': sections,
                'accountVisibility': user.visibility
            };
            return res.send(200, visibilities);
        });
    });
});

// Get a specific profile section.
OAE.tenantRouter.on('get', '/api/user/:id/profile/:section', function(req, res) {
    ProfileAPI.getSection(req.ctx, req.params.id, req.params.section, function(err, profileSection) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(200, profileSection.data);
    });
});

// Create or update a section.
OAE.tenantRouter.on('post', '/api/user/:id/profile/:section', function(req, res) {
    var overwrite = (req.body.overwrite === 'true' || req.body.overwrite === true);
    var data = req.body.data;
    // Try parsing the data.
    try {
        data = JSON.parse(data);
    } catch (err) {
        return res.send(400, 'The data needs to be a proper JSON object.');
    }

    ProfileAPI.setSection(req.ctx, req.params.id, req.params.section, req.body.visibility, data, overwrite, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        res.send(200);
    });
});

// Update the visibility of a section.
OAE.tenantRouter.on('post', '/api/user/:id/profile/:section/visibility', function(req, res) {
    ProfileAPI.setSection(req.ctx, req.params.id, req.params.section, req.body.visibility, {}, false, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        res.send(200);
    });
});
