var Publication = module.exports.Publication = function(id, displayName, publicationType, date, thumbnailUri, authors, publisher, extra) {
    var that = {};
    extra = extra || {};

    // The OAE identifier for this publication
    that.id = id;

    // The title or display name for this publication
    that.displayName = displayName;

    // One of `PublicationConstants.publicationTypes`
    that.publicationType = publicationType;
    that.date = date;
    that.thumbnailUri = thumbnailUri;

    that.authors = authors;

    // Although the name is slightly ambiguous, this field is intended for Journal Names, Book publishers, ..
    that.publisher = publisher;

    // Extra options that depend on the publicationType

    // One of `null`, `gold` or `silver`
    that.openAccessType = extra.openAccessType;

    // In case the publication is part of a serial (Journal, Book, ..)
    that.issueNumber = extra.issueNumber || null;
    that.pageBegin = extra.pageBegin || null;
    that.pageEnd = extra.pageEnd || null;

    return that;
};

var IngesterPublication = module.exports.IngesterPublication = function(authorName, sourceIds, publication) {
    var that = {};
    that.authorName = authorName;
    that.sourceIds = sourceIds;
    that.publication = publication;
    return that;
};


