var util = require('oae-roles/lib/util');

/**
 * A Principal object that represents a principal in the roles system.
 *
 * @param {String} principalType The type of this principal (e.g., 'u' for User, 'g' for Group)
 * @param {String} tenantId The ID of the tenant to which this principal belongs
 * @param {String} principalId The ID of this principal
 * @return {Object} The principal Object
 */
module.exports.Principal = function(principalType, tenantId, principalId) {
    var that = {};

    that.tenantId = tenantId;
    that.principalType = principalType;
    that.principalId = principalId;

    /**
     * Get the UUID of this principal instance.
     */
    that.toUuid = function() {
        return util.toUuid(principalType, tenantId, principalId);
    }

    return that;
}

/**
 * A Resource object that represents a resource to which a principal may be assigned a role in the system.
 *
 * @param {String} resourceType The type of this principal (e.g., 'g' for Group, 'c' for Content)
 * @param {String} tenantId The ID of the tenant to which this resource belongs
 * @param {String} resourceId The ID of this resource
 * @return {Object} The resource Object
 */
module.exports.Resource = function(resourceType, tenantId, resourceId) {
    var that = {};

    that.tenantId = tenantId;
    that.resourceType = resourceType;
    that.resourceId = principalId;

    /**
     * Get the UUID of this resource instance.
     */
    that.toUuid = function() {
        return util.toUuid(resourceType, tenantId, resourceId);
    }

    return that;
}