var model = require('oae-roles/lib/model');

/**
 * Construct a resource based on the given UUID.
 *
 * @param {String} uuid A UUID of a resource
 * @return {Object} A model.Resource object derived from this UUID
 */
module.exports.getResourceFromUuid = function(uuid) {
    return unpackUuid(model.Resource, uuid);
};

/**
 * Construct a Principal based on the given UUID.
 *
 * @param {String} uuid A UUID of a principal
 * @return {Object} A model.Principal object derived from this UUID
 */
module.exports.getPrincipalFromUuid = function(uuid) {
    return unpackUuid(model.Principal, uuid);
};

module.exports.toUuid = function(type, tenantId, instanceId) {
    return type+':'+tenantId+':'+instanceId;
};

// convenienct method to combine the similar operation of splitting a UUID
function unpackUuid(type, uuid) {
    var split = uuid.split(':');
    return new type(split[0], split[1], split[2]);
}
