var Cassandra = require('oae-util/lib/cassandra');

var cassandraConfig = {
    'type': 'pool', // pool or simple
    'hosts': ['127.0.0.1:9160'], 
    'port': 9160, 
    'keyspace': 'oae',
    'system': '127.0.0.1:9160',
    'user': '', 
    'pass': ''
};

Cassandra.init(cassandraConfig, function() {
    require('oae-roles/lib/dataload').loadSequential('cam', 'mrvisser', 'pXyur3', 50, 50, 50, function() {
        console.log('Data loading complete.');
        process.exit(0);
    });
});
