var SearchConstants = module.exports.SearchConstants = {};

SearchConstants.sort = {
    'OPTIONS': ['asc', 'desc'],
    'ASC': 'asc',
    'DESC': 'desc'
};

SearchConstants.query = {
    'ALL': '*',
    'MINIMUM_SCORE': 0.20
};

SearchConstants.general = {

    // The different types of resources that can be filtered
    'RESOURCE_TYPE_ALL': 'all',
    'RESOURCE_TYPE_CONTENT': 'content',
    'RESOURCE_TYPE_GROUP': 'group',
    'RESOURCE_TYPE_USER': 'user',

    // A search scope that includes resources for all tenants, including
    // private tenants / tenants outside of the current tenant's network
    'SCOPE_ALL': '_all',

    // A search scope that includes resources for tenants within the
    // current tenant's network, as well as resources the user has
    // explicit access to outside the tenant network
    'SCOPE_NETWORK': '_network',

    // A search scope that includes only resources with which a user can
    // interact (e.g., can comment on, follow, share with, etc...)
    'SCOPE_INTERACT': '_interact',

    // A search scope that includes resources for only the current tenant
    // in addition to resources the user has access to outside the tenant
    'SCOPE_TENANT': '_tenant'
};

SearchConstants.search = {
    'MAPPING_RESOURCE': 'resource'
};

SearchConstants.mq = {
    'TASK_INDEX_DOCUMENT': 'oae-search/index',
    'TASK_DELETE_DOCUMENT': 'oae-search/delete',
    'TASK_REINDEX_ALL': 'oae-search/reindex'
};
